import React,{useState} from 'react'
import styled from 'styled-components'
import Username from '../Username'
import axios from 'axios'
import {useHistory} from 'react-router-dom'

const Connexion = props => {
  const [formState, setFormState]= useState ({username:'', password:''})
  //const [errorMessage, setErrorMessage] =useState('')
  const history =useHistory()

  const submit = e =>{
    e.preventDefault()
   axios ({
       method:'POST',
       url: 'https://easy-logging-api.herokuapp.com/users/login',
       data:{
           username: formState.username,
           password: formState.password
       }
   })
   .then(res =>{
       localStorage.setItem ('token',res.headers['x-access-token'])
       history.push('/post')
   }) 
}

  return (
    <StyledForm>
      <FormSpan type='text'></FormSpan >
      <FormInput placeholder='Username' onChange={e => setFormState({...formState, username:e.target.value})} type='text'></FormInput>
      <FormInput placeholder='Password' onChange={e => setFormState({...formState, password:e.target.value})} type='password'></FormInput>
      <FormInput type='submit'></FormInput>
    </StyledForm>
  )
}

const FormSpan =styled.span`
  color: green;
  margin-bottom: 10px;
  `
const StyledForm =styled.form`
  display:flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  `
const FormInput = styled.input`
  margin: 10px;
  border-radius: 15px;
  
  background-color: darkgray;
  height: 30px;
  color:white;
  padding: 0px 6px;
  `
export default Connexion;
