import React from 'react'
import styled from 'styled-components'
import Username from '../Username'
import Avatar from '../Avatar'
import Picture from '../Picture'
import Caption from '../Caption'
import Header from '../Header'

const FakeData = [
  {
    name: 'Patick Bruel',
    urlAvatar:
      'https://www.nj.com/resizer/zovGSasCaR41h_yUGYHXbVTQW2A=/1280x0/smart/cloudfront-us-east-1.images.arcpublishing.com/advancelocal/SJGKVE5UNVESVCW7BBOHKQCZVE.jpg',
    urlImage:
      'https://mp-prod-de-medialib.s3-eu-central-1.amazonaws.com/motivwelt/milieu/fenster-betonwand-frontal/naturbild/4-3/no-frame/naturbild-xl.jpg',
    captionName: 'Jean Claude',
    captionComment: "Bonjour c'est beau"
  },
  {
    name: 'Patick Bosso',
    urlAvatar:
      'https://encrypted-tbn0.gstatic.com/images?q=tbn%3AANd9GcRcxrCVF5VKaTObPqxLclZtp7lrAmE4xo9q0g&usqp=CAU',
    urlImage:
      'https://mp-prod-de-medialib.s3-eu-central-1.amazonaws.com/motivwelt/stockimage/lachende-haselmaus/frontal-view/lachende-haselmaus-l.jpg',
    captionName: 'Jean Claude Vandamme',
    captionComment: "y'a des oiseaux"
  }
]
const Post = props => {
  return (
    <div>
      {FakeData.map(data => {
        return (
        <PostContainer>
          <div>
            <Username name={data.name} />
            <Avatar url={data.urlAvatar}/>
          </div>
          <div>
            <Picture url={data.urlImage}></Picture>
            <Caption nom={props.nom} caption={props.caption} />
          </div>
        </PostContainer>
      )})}
    </div>
  )
}

const PostImg = styled.img`
  align-items: center;
  padding: 20px;
  widh: 100px;
`

const PostContainer = styled.div`
  display: flex;
  align-items: center;
  padding: 20px;
  max-width: 500px;
  border: 1px solid lightgray;
  margin-bottom: 45px;
`

export default Post
