import React, {useState} from 'react';

function ImageUpload () {
    const [image, setImage] = useState(null);
    const [progress, setProgress] = useState(0);
    const [caption, setCaption] = useState('');
    
    const handleChange =(e) =>{
        if (e.target.file[0]){
            setImage(e.target.file[0]);
        }   
    };

    <input type="file" accept="image/x-png,image/jpeg,image/gif" 
    onChange={() => this.upload()}/>



    return (
        <div>
            <h1>Hello image</h1>

            <input type="text" placeholder='Entrez un commentaire' onChange={event => setCaption(event.target.value)} value="caption"/> 
            <input type="file" OnChange={handleChange}/>
            <input type="file" accept="image/x-png,image/jpeg,image/gif" onChange={() => this.upload()}/>
        </div>
    );
};

export default ImageUpload;
