import React from 'react';
import styled from 'styled-components';
import Caption from '../Caption';
import Picture from '../Picture';


const PictureCom = props => {
    return (
           
        <PictureContainer>
            <Picture url='https://fr.davidsuzuki.org/wp-content/uploads/sites/3/2013/05/connecter-nature.jpg'></Picture>
            <Caption name2={props.nom, props.caption}></Caption>
        </PictureContainer>
        
    );
};


const PictureContainer =styled.div`
    display: block;
    align-items: center;
    padding:20px;
`



export default PictureCom;
