import React from 'react';
import styled from 'styled-components';
import Username from '../Username';
import Avatar from '../Avatar'
import Picture from '../Picture'
import Caption from '../Caption'

const Post = props => {
    return (
           
        <PostContainer>
            <div> 
                <Username name='Alexandre'/>
                <Avatar url='https://www.web-soluces.net/webmaster/avatar/AvatarMakerCom-Garcon.png'/>
            </div>    
            <div className>
                <Picture url='https://fr.davidsuzuki.org/wp-content/uploads/sites/3/2013/05/connecter-nature.jpg'></Picture>
                <Caption nom={props.nom} caption={props.caption} ></Caption>
            </div>        
        </PostContainer>
      
        
    );
};


const PictureDiv =styled.div 
`display: block,
align-items: center,
padding:15px,
`
  

const PostImg =styled.img`
align-items: center;
padding:20px;
widh:100px;
`

const PostContainer =styled.div`
    display: flex;
    align-items: center;
    padding:20px;
`



export default Post;
